// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;


interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
}

contract WikenSwapEth {
    IERC20 public erc20Token;
    address public owner = 0x9C7E2DDfbD8aec5b76E8baF5DC4b7B58CE012289;
    mapping(address => uint256) public deposits;

    event Deposit(address indexed user, uint256 amount, string targetAddress);
    event SwapCompleted(address indexed user, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not the owner");
        _;
    }

    constructor() {
        erc20Token = IERC20(0x2680206b70e5B3E8c1880AAB8C804f9ce83CBF30);
    }

    function withdrawTokens(address recipient, uint256 amount) external onlyOwner {
        uint256 minAmount = 10000 * (10 ** 18);
        require(amount >= minAmount, "Minimum swap amount is 10,000");
        require(erc20Token.transfer(recipient, amount), "Transfer failed");
        emit SwapCompleted(recipient, amount);
    }
}
