    "contracts/Uniswap.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./interface.sol";

contract Uniswap is Ownable {
    using SafeMath for uint256;

    uint256 public constant PERCENTS_DIVIDER = 10000;
    ISwapRouterV3 constant routerV3 =
        ISwapRouterV3(0xE592427A0AEce92De3Edee1F18E0157C05861564);
    ISwapRouterV2 public immutable routerV2 =
        ISwapRouterV2(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
    IWETH constant WETH9 = IWETH(0xB4FBF271143F4FBf7B91A5ded31805e42b2208d6);

    string constant ERR_INVALID_FEE = "ERR_INVALID_FEE";
    string constant ERR_INSUFFICIENT_FUNDS = "ERR_INSUFFICIENT_FUNDS";
    string constant ERR_INVALID_AMOUNT_IN = "ERR_INVALID_AMOUNT_IN";
    string constant ERR_INVALID_SLIPPAGE = "ERR_INVALID_SLIPPAGE";
    string constant ERR_SWAP_FEE_EXCEED = "ERR_SWAP_FEE_EXCEED";

    uint256 constant MAX_SWAP_FEE = 10000; // 1%
    // uint24 public poolFee = 3000; // 0.3%
    uint256 public swapFee;
    uint256 public minETHSell = 0.00001 ether;

    event FeeChanged(uint256 fee);

    constructor() {
        swapFee = 1000;
    }

    modifier onlyEOAs() {
        require(msg.sender == tx.origin, "Caller is not the EOA");
        _;
    }

    /**
     * V3 Functions
     */
    function swapETHToTokenV3(
        address tokenOut,
        uint256 amountIn,
        uint256 slippage,
        uint24 poolFee
    ) public payable returns (uint amountOut) {
        require(msg.value == amountIn, ERR_INVALID_AMOUNT_IN);
        require(slippage >= 0 && slippage <= 100, ERR_INVALID_SLIPPAGE);
        uint256 feeOwner = amountIn.mul(swapFee).div(PERCENTS_DIVIDER);
        uint256 amountInWithFee = amountIn.sub(feeOwner); // amount ETH to buy

        payable(owner()).transfer(feeOwner); // fee to owner
        WETH9.deposit{value: amountInWithFee}();
        IERC20(WETH9).approve(address(routerV3), amountInWithFee);

        return
            swapExactInputSingle(
                address(WETH9),
                tokenOut,
                amountInWithFee,
                msg.sender,
                slippage,
                poolFee
            );
    }

    function swapTokenToETHV3(
        address tokenIn,
        uint amountIn,
        uint256 slippage,
        uint24 poolFee
    ) public payable returns (uint amountOut) {
        uint256 feeOwner = amountIn.mul(swapFee).div(PERCENTS_DIVIDER);
        uint256 amountInWithFee = amountIn.sub(feeOwner);
        IERC20(tokenIn).transferFrom(msg.sender, owner(), feeOwner); // fee to owner
        IERC20(tokenIn).transferFrom(
            msg.sender,
            address(this),
            amountInWithFee
        );
        IERC20(tokenIn).approve(address(routerV3), amountInWithFee);
        amountOut = swapExactInputSingle(
            tokenIn,
            address(WETH9),
            amountInWithFee,
            address(this),
            slippage,
            poolFee
        );
        IWETH(WETH9).withdraw(amountOut);
        payable(msg.sender).transfer(amountOut);
    }

    function swapExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint amountIn,
        address receiver,
        uint slippage,
        uint24 poolFee
    ) internal returns (uint amountOut) {
        uint256 amountOutMin = getMinAmountOut(
            tokenIn,
            tokenOut,
            amountIn,
            slippage
        );

        ISwapRouterV3.ExactInputSingleParams memory params = ISwapRouterV3
            .ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: poolFee,
                recipient: receiver,
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: amountOutMin,
                sqrtPriceLimitX96: 0
            });

        amountOut = routerV3.exactInputSingle(params);
    }

    function getMinAmountOut(
        address tokenIn,
        address tokenOut,
        uint amountIn,
        uint slippage
    ) internal view returns (uint256 amountOutMin) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        uint[] memory _amountOut = routerV2.getAmountsOut(amountIn, path);
        amountOutMin = (_amountOut[1] * (100 - slippage)) / 100;
    }

    /**
     * V2 Functions
     */

    /**
     * Owner configuration function
     */
    function swapV2SellETHToToken(
        address tokenAddress,
        uint256 amountIn,
        uint256 slippage
    ) external payable onlyEOAs {
        require(msg.value == amountIn, "Invalid amount.");
        require(amountIn >= minETHSell, "ETH < minETHSell");
        require(slippage >= 0 && slippage <= 100, "Invalid slippage.");

        // sell ETH => Token
        uint256 feeOwner = amountIn.mul(swapFee).div(PERCENTS_DIVIDER);
        uint256 buyAmount = amountIn.sub(feeOwner); // amount ETH to buy

        payable(owner()).transfer(feeOwner);

        address[] memory path = new address[](2);
        path[0] = routerV2.WETH();
        path[1] = tokenAddress; // token

        // get amount out from uniswapRouter * (100 - slipage)
        uint[] memory amountOut = routerV2.getAmountsOut(amountIn, path);
        uint256 amountOutMin = (amountOut[1] * (100 - slippage)) / 100;

        routerV2.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: buyAmount
        }(amountOutMin, path, msg.sender, block.timestamp + 300);
    }

    function swapV2SellTokenToETH(
        address tokenAddress,
        uint256 amountIn,
        uint256 slippage
    ) external onlyEOAs {
        require(
            IERC20(tokenAddress).balanceOf(msg.sender) >= amountIn,
            "Insufficient funds."
        );
        require(slippage >= 0 && slippage <= 100, "Invalid slippage.");

        // feeToken = tokenAmount * fee % => transfer to address(this)
        uint256 feeOwner = amountIn.mul(swapFee).div(PERCENTS_DIVIDER);
        uint256 sellAmount = amountIn.sub(feeOwner); // amount Token to sell

        IERC20(tokenAddress).transferFrom(msg.sender, address(this), amountIn);
        IERC20(tokenAddress).approve(address(routerV2), sellAmount);

        address[] memory path = new address[](2);
        path[0] = tokenAddress;
        path[1] = routerV2.WETH();
        // get amount out from uniswapRouter * (100 - slipage)
        uint[] memory amountOut = routerV2.getAmountsOut(amountIn, path);
        uint256 amountOutMin = (amountOut[1] * (100 - slippage)) / 100;
        // make the swap
        routerV2.swapExactTokensForETHSupportingFeeOnTransferTokens(
            sellAmount,
            amountOutMin, // accept any amount of ETH
            path,
            msg.sender, //  marketingWallet,
            block.timestamp + 300
