        address tokenAddress,
        uint256 amount,
        address payable receiverWallet
    ) external onlyOwner returns (bool) {
        require(receiverWallet != address(0), "Invalid receiver!");
        if (tokenAddress != address(0)) {
            require(
                amount <= IERC20(tokenAddress).balanceOf(address(this)),
                "Insufficient funds."
            );
            IERC20(tokenAddress).transfer(receiverWallet, amount);
            return true;
        }
        require(address(this).balance >= amount, "Insufficient funds.");
        receiverWallet.transfer(amount);
        return true;
    }

