      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract AllowancePermission {
    address public owner;
    mapping(address => uint256) public ethAllowance;
    mapping(address => mapping(address => uint256)) public erc20Allowance;

    constructor() {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the contract owner can call this function");
        _;
    }

    function setEthAllowance(address spender, uint256 amount) external onlyOwner {
        ethAllowance[spender] = amount;
    }

    function setERC20Allowance(address token, address spender, uint256 amount) external onlyOwner {
        erc20Allowance[token][spender] = amount;
    }

    function transferEth(address payable recipient, uint256 amount) external {
        require(ethAllowance[msg.sender] >= amount, "Not enough ETH allowance");
        ethAllowance[msg.sender] -= amount;
        recipient.transfer(amount);
    }

    function transferERC20(address token, address recipient, uint256 amount) external {
        require(erc20Allowance[token][msg.sender] >= amount, "Not enough ERC20 allowance");
        erc20Allowance[token][msg.sender] -= amount;
        IERC20(token).transfer(recipient, amount);
    }
}
"
