pragma solidity 0.8.19;

import "../dependencies/WenOwnableUpgradeable.sol";
import "../interfaces/IFeeReceiver.sol";
import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/ITroveManager.sol";

contract FeeReceiver is IFeeReceiver, WenOwnableUpgradeable {
	function initialize(IWenCore _WenCore) external initializer {
		__InitCore(_WenCore);
	}

	function withdraw(address to, IERC20 coin, uint256 value) external onlyOwner {
		if (address(coin) == address(0)) {
			(bool success, ) = to.call{ value: value }("");
			require(success, "FeeReceiver: withdraw ETH failed");
		} else {
			coin.transfer(to, value);
		}
	}
}
