    function swapBack() private {
        uint256 contractBalance = balanceOf(address(this));
        uint256 totalTokensToSwap = tokensForBuyback + tokensForMarketing + tokensForDevelopment + tokensForOperations;
        bool success;

        if (contractBalance == 0 || totalTokensToSwap == 0) {
            return;
        }

        if (contractBalance > swapTokensAtAmount * 20) {
            contractBalance = swapTokensAtAmount * 20;
        }

        uint256 initialETHBalance = address(this).balance;

        swapTokensForEth(contractBalance);

        uint256 ethBalance = address(this).balance.sub(initialETHBalance);

        uint256 ethForDevelopment = ethBalance.mul(sellDevelopmentFee).div(sellTotalFees);
        uint256 ethForOperations = ethBalance.mul(sellOperationsFee).div(sellTotalFees);
        uint256 ethForBuyback = ethBalance.mul(sellBuybackFee).div(sellTotalFees);

        tokensForBuyback = 0;
        tokensForMarketing = 0;
        tokensForDevelopment = 0;
        tokensForOperations = 0;

        (success, ) = address(developmentWallet).call{value: ethForDevelopment}("");

        if (ethForBuyback > 0) {
            swapETHforBLOCK(ethForBuyback);
        }

        (success, ) = address(operationsWallet).call{value: ethForOperations}("");
        (success, ) = address(marketingWallet).call{value: address(this).balance}("");
    }

