    "src/airdrop/BoxAggregator.sol": {
      "content": "//SPDX-License-Identifier: MIT\npragma solidity ^0.8.0;\n\nimport {IBox} from \"../interfaces/IBox.sol\";\n\ncontract BoxAggregator {\n    function claimAirdropBatch(\n        address[] memory boxes_,\n        bytes32[][] memory proofs_,\n        address[] memory claimers_,\n        uint256[] memory totalValues_,\n        address[] memory airdrops_,\n        uint256[] memory values_\n    ) public {\n        for (uint256 i = 0; i < boxes_.length; i++) {\n            IBox(boxes_[i]).claimAirdrop(\n                proofs_[i],\n                claimers_[i],\n                totalValues_[i],\n                airdrops_[i],\n                values_[i]\n            );\n        }\n    }\n}\n"
    },
    "src/interfaces/IBox.sol": {
      "content": "//SPDX-License-Identifier: MIT\npragma solidity ^0.8.0;\n\ninterface IBox {\n    function claimAirdrop(\n        bytes32[] memory proof_,\n        address claimer_,\n        uint256 totalValue_,\n        address airdrop_,\n        uint256 value_\n    ) external;\n\n    function claimAirdropBatch(\n        bytes32[][] memory proofs_,\n        address[] memory claimers_,\n        uint256[] memory totalValues_,\n        address[] memory airdrops_,\n        uint256[] memory values_\n    ) external;\n}\n"
    }
