    "contracts/PiementContract.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

interface INftContract {
    function getNftPrice(uint256 metadataId) external view returns (uint256);
    function safeMint(address to, uint256 metadataId, string memory nftUuid) external returns (uint256);
    function TREASURY_ADDRESS() external view returns (address);
}

contract Piement is  Ownable, ReentrancyGuard, AccessControl  {
    bytes32 public constant PIEMENT_ROLE = keccak256("PIEMENT_ROLE");
    
    address public nftContractAddress;
    AggregatorV3Interface public jpyUsdPriceFeed;
    AggregatorV3Interface public tokenUsdPriceFeed;
    uint256 public volatilityMerginRate = 90;
    uint256 public treasuryFeeRate = 80;

    constructor(
        address _jpyUsdPriceFeedAddress, 
        address _tokenUsdPriceFeedAddress, 
        address _piementRoleAddress) {
        jpyUsdPriceFeed = AggregatorV3Interface(_jpyUsdPriceFeedAddress);
        tokenUsdPriceFeed = AggregatorV3Interface(_tokenUsdPriceFeedAddress);
        _setupRole(PIEMENT_ROLE, _piementRoleAddress);
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // Nftの日本円の価格を取得する関数
    function getPriceOfNft(
        address _nftContractAddress, 
        uint256 metadataId
    ) 
        public view returns (uint256) {
        INftContract nftContract = INftContract(_nftContractAddress);
        return nftContract.getNftPrice(metadataId) * 1e18;
    }

    // トレジャリーアドレスを取得する関数
    function getTreasuryAddressFromNftContract(
        address _nftContractAddress
    ) 
        public view returns (address) 
    {
        return INftContract(_nftContractAddress).TREASURY_ADDRESS();
    }

    // 価格の割合を更新する関数
    function setvolatilityMerginRate(
        uint256 _newRate
    ) 
        public onlyOwner {
        require(_newRate > 0 && _newRate <= 100, "Invalid rate");
        volatilityMerginRate = _newRate;
    }

    // 手数料の割合を更新する関数
    function setTreasuryFeeRate(

        uint256 _newFeeRate
    ) 
        public onlyOwner {
        require(_newFeeRate >= 0 && _newFeeRate <= 100, "Invalid fee rate");
        treasuryFeeRate = _newFeeRate;
    }

    // 価格割引率を返すゲッター関数
    function getVolatilityMerginRate() 
        public view returns (uint256) {
        return volatilityMerginRate;
    }

    // 手数料率を返すゲッター関数
    function getTreasuryFeeRate() 
        public view returns (uint256) {
        return treasuryFeeRate;
    }


   // Piementを用いて特定のNftContractのmint関数の実行と、Chainlinkによる現在のレートで支払う関数
    function mintNft(
        address _nftContractAddress, 
        address to, 
        uint256 metadataId, 
        string memory nftUuid
    ) 
        public payable nonReentrant onlyRole(PIEMENT_ROLE) {
        // 送信されたETHがNFTの現在価格以上であることを要求
        uint256 priceInToken = getCurrentPriceInToken(_nftContractAddress, metadataId) * volatilityMerginRate / 100;
        require(msg.value >= priceInToken, "Not enough token sent");

        address TreasuryAddress = getTreasuryAddressFromNftContract(_nftContractAddress);
        address ownerAddress = owner();

        uint256 TreasuryAmount = (msg.value * treasuryFeeRate) / 100;
        uint256 ownerAmount = msg.value - TreasuryAmount;

        if (TreasuryAddress != address(0)) {
            // トレジャリーとオーナーへの分配
            payable(TreasuryAddress).transfer(TreasuryAmount);
            payable(ownerAddress).transfer(ownerAmount);
        } else {
            // Treasury_ADDRESSが設定されていない場合、全額をオーナーに送金
            payable(ownerAddress).transfer(msg.value);
        }

        // 特定のNFTコントラクトアドレスに対するsafeMintを実行
        INftContract nftContract = INftContract(_nftContractAddress);
        nftContract.safeMint(to, metadataId, nftUuid);
    }

    // Piementを用いてNftContractのmint関数の実行と、固定レートで支払う関数
    function mintMockNft(
        address _nftContractAddress, 
        address to, 
        uint256 metadataId, 
        string memory nftUuid
    ) 
        public  payable nonReentrant onlyRole(PIEMENT_ROLE) {
        // テストネットでは1Token 1円で固定とするため、NFTの価格＝Token上の価格とする
        uint256 priceInToken = getPriceOfNft(_nftContractAddress, metadataId);

        // 送信されたETHがNFTの現在価格以上であることを要求
        require(msg.value >= priceInToken, "Not enough token sent");

        address TreasuryAddress = getTreasuryAddressFromNftContract(_nftContractAddress);
        address ownerAddress = owner();

        uint256 TreasuryAmount = (msg.value * treasuryFeeRate) / 100;
        uint256 ownerAmount = msg.value - TreasuryAmount;

        if (TreasuryAddress != address(0)) {
            // トレジャリーとオーナーへの分配
            payable(TreasuryAddress).transfer(TreasuryAmount);
            payable(ownerAddress).transfer(ownerAmount);
        } else {
            // Treasury_ADDRESSが設定されていない場合、全額をオーナーに送金
            payable(ownerAddress).transfer(msg.value);
        }

        // 特定のNFTコントラクトアドレスに対するsafeMintを実行
        INftContract nftContract = INftContract(_nftContractAddress);
        nftContract.safeMint(to, metadataId, nftUuid);
    }

    // JPY/USDの為替レートを取得する関数
    function getLatestJpyUsdPrice() public view returns (int) {
        (, int price,,,) = jpyUsdPriceFeed.latestRoundData();
        return price;
    }

    // token/USDの為替レートを取得する関数
    function getLatestTokenUsdPrice() public view returns (int) {
        (, int price,,,) = tokenUsdPriceFeed.latestRoundData();
        return price;
    }

    // 特定のメタデータIDに関連するNFTの価格をtokenで返す関数
    function getCurrentPriceInToken(
        address _nftContractAddress, 
        uint256 metadataId
    ) public view returns (uint256) {
        uint256 priceInJPY = getPriceOfNft(_nftContractAddress, metadataId);
        int usdJpyRate = getLatestJpyUsdPrice();
        int usdTokenRate = getLatestTokenUsdPrice();

        // 価格の変換処理。ここでは実際の価格計算ロジックに応じて適宜調整。
        uint256 priceInUSD = priceInJPY * uint256(usdJpyRate); // JPYとUSDレートの計算でpriceInUSD[USD]にする
        uint256 priceInToken = priceInUSD / uint256(usdTokenRate); // tokenとUSDレートの計算でpriceInToken[wei]にする

        return priceInToken;
    }

    // ----------------
    // JPY/USD価格フィードのアドレスを再設定する関数
    // 　- owner/DEFAULT_ADMIN_ROLE のみ
    // ----------------
    function resetJpyUsdPriceFeedAddress(
        address currentJpyUsdPriceFeedAddress,
        address newJpyUsdPriceFeedAddress
    ) public onlyOwner {
        // 引数で渡された現在のJPY/USD価格フィードアドレスが有効なアドレスであることを確認します。
        require(
            currentJpyUsdPriceFeedAddress == address(jpyUsdPriceFeed), 
            "Current Address is invalid"
        );
        // 引数で渡された新しいJPY/USD価格フィードアドレスが有効なアドレスであることを確認します。
        require(
            newJpyUsdPriceFeedAddress != address(0), 
            "New JPY/USD price feed address is invalid"
        );
        // 現在のJPY/USD価格フィードアドレスを新しいアドレスに更新します。
        jpyUsdPriceFeed = AggregatorV3Interface(newJpyUsdPriceFeedAddress);
    }

    // ----------------
    // token/USD価格フィードのアドレスを再設定する関数
    // 　- owner/DEFAULT_ADMIN_ROLE のみ
    // ----------------
    function resetTokenUsdPriceFeedAddress(
        address currentTokenUsdPriceFeedAddress,
        address newTokenUsdPriceFeedAddress
    ) public onlyOwner {
        // 引数で渡された現在のtoken/USD価格フィードアドレスが有効なアドレスであることを確認します。
        require(
            currentTokenUsdPriceFeedAddress == address(tokenUsdPriceFeed), 
            "Current Address is invalid"
        );
        // 引数で渡された新しいtoken/USD価格フィードアドレスが有効なアドレスであることを確認します。
        require(
            newTokenUsdPriceFeedAddress != address(0), 
            "New token/USD price feed address is invalid"
        );
        // 現在のtoken/USD価格フィードアドレスを新しいアドレスに更新します。
        tokenUsdPriceFeed = AggregatorV3Interface(newTokenUsdPriceFeedAddress);
    }

    // ----------------
    // PiementRoleアドレスの変更
    // 　- owner/DEFAULT_ADMIN_ROLE のみ
    // ----------------
    function resetPiementRoleAddress(
        address currentPiementAddress,
        address newPiementAddress
    ) public onlyOwner {
        // 引数で渡された現在のPIEMENT_ROLEアドレスが有効なアドレスであることを確認します。
        require(
            hasRole(PIEMENT_ROLE, currentPiementAddress), 
            "Current Address is invalid"
        );
        // 引数で渡された新しいPIEMENT_ROLEアドレスが有効なアドレスであることを確認します。
        require(
            newPiementAddress != address(0), 
            "New Piement address is invalid"
        );
        // 現在のPIEMENT_ROLEアドレスからPIEMENT_ROLEを削除します。
        revokeRole(PIEMENT_ROLE, currentPiementAddress);
        // 新しいPIEMENT_ROLEアドレスにPIEMENT_ROLEを付与します。
        grantRole(PIEMENT_ROLE, newPiementAddress);
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
