      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/// @author: yungwknd
/// @artist: alphacentaurikid

import "@manifoldxyz/marketplace-solidity/contracts/ILazyDelivery.sol";
import "@manifoldxyz/marketplace-solidity/contracts/ILazyDeliveryMetadata.sol";
import "@manifoldxyz/libraries-solidity/contracts/access/AdminControl.sol";

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
/////////////////////////////////////////////////////////////////////////////////////////
//                                                                                     //
//    NO TOKEN                                                                         //
//                                                    Developed by ACK + yungwknd      //
//                                                                                     //
/////////////////////////////////////////////////////////////////////////////////////////
contract NoToken is AdminControl, ILazyDelivery, ILazyDeliveryMetadata {

  mapping(uint => string) public assetURIs;

  function configureSale(uint assetId, string memory uri) public adminRequired {
    require(bytes(assetURIs[assetId]).length == 0, "Cannot re-use assetId");
    assetURIs[assetId] = uri;
  }

  function supportsInterface(bytes4 interfaceId) public view virtual override(AdminControl, IERC165) returns (bool) {
      return
      interfaceId == type(ILazyDelivery).interfaceId ||
      interfaceId == type(ILazyDeliveryMetadata).interfaceId ||
      AdminControl.supportsInterface(interfaceId) || super.supportsInterface(interfaceId);
  }

  /**
   * No token, nothing to deliver 
   */
  function deliver(uint40, address, uint256, uint24, uint256, address, uint256) external override {
  }

  function assetURI(uint256 assetId) external view override returns(string memory) {
    return assetURIs[assetId];
  }
}"
