            elapsedTime = block.timestamp - TGEStartDate;
        }else{
            elapsedTime = block.timestamp - investor.lastWithdrawalTimestamp;
        }
        uint256 minutesElapsed = elapsedTime / 1 days;
        uint256 tokensToWithdraw;
        if(minutesElapsed >= 1){
             tokensToWithdraw = minutesElapsed * investor.airdropTokensPerDay;
        }else{
             tokensToWithdraw = 0;
        }
        if (tokensToWithdraw > investor.airdropvestingBalance) {
            tokensToWithdraw = investor.airdropvestingBalance;
        }
