        require(_token != address(0), "Invalid Token Address");
        token = IERC20Upgradeable(_token);
        emit setTokens (_token);
    }
    function initialize(address Signer,uint256 _startDate,address _tokenAddress) external initializer{
        require(_tokenAddress != address(0));
        TGEStartDate = _startDate;
        isStart = true;
         token = IERC20Upgradeable(_tokenAddress);
        signer = Signer;
        __Ownable_init();
        __ReentrancyGuard_init();
    }
     modifier onlyPendingOwner() {
        require(msg.sender == pendingOwner, "Caller is not the pending owner");
