      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { AccessControlInternal } from "@solidstate/contracts/access/access_control/AccessControlInternal.sol";

contract FeeContract is Ownable, AccessControlInternal {
    bytes32 internal constant SWAP_ROLE = keccak256("SWAP_ROLE");
    event SwapResult(bool success, bytes data);

    function addSwapRole(address account) external onlyOwner {
        _grantRole(SWAP_ROLE, account);
    }

    function removeRole(address account) external onlyOwner {
        _revokeRole(SWAP_ROLE, account);
    }

    function getSwapRoleMember(uint256 index) public view returns (address) {
        return _getRoleMember(SWAP_ROLE, index);
    }

    function getSwapRoleMemberCount() public view returns (uint256) {
        return _getRoleMemberCount(SWAP_ROLE);
    }

    function swap(address router, address _targetTokenAddress, bytes calldata _data) external onlyRole(SWAP_ROLE) {
        IERC20 targetToken = IERC20(_targetTokenAddress);
        uint256 balance = targetToken.balanceOf(address(this));
        targetToken.approve(router, 0);
        targetToken.approve(router, balance);

        (bool swapSuccess, bytes memory data) = router.call(_data);
        targetToken.approve(router, 0);
        emit SwapResult(swapSuccess, data);

        if (!swapSuccess) {
            revert("Swap failed.");
        }
    }

    function transferNative(address _to, uint256 _amount) public onlyOwner {
        if (_amount == type(uint256).max) {
            _amount = address(this).balance;
        }

        (bool success, bytes memory result) = _to.call{ value: _amount }("");
        if (!success) {
            string memory errorMessage = string(result);
            revert(errorMessage);
        }
    }

    function emergencyWithdraw(address _token, address _to, uint256 _amount) public onlyOwner {
        IERC20 token = IERC20(_token);
        uint256 balance = token.balanceOf(address(this));
        if (_amount == type(uint256).max) {
            _amount = balance;
        }

        token.transfer(_to, balance);
    }

    receive() external payable {
        // This function is executed when a contract receives plain Ether (without data)
    }
}
